            function TopSearch(type){
                if($("input[name=keyword]").val() == 'keyword or location'){
                    if(type == 'profile')	window.location = "'/interns/?keyword=";
                    else if(type == 'job')	window.location = "/jobs/?keyword=";
                }
                else{
                    if(type == 'profile')	window.location = "/interns/?keyword="+ $('.overlay_search').val();
                    else	window.location = "/jobs/?keyword="+ $('.overlay_search').val();
                }
            }
            $(function(){
                var url = window.location.href;
                var nohttp = url.split('/');
                var pageName = nohttp[nohttp.length - 1];
                if(pageName == '')
                    pageName = 'home';
                if(pageName == 'create-profile')
                    pageName = 'my-profile';
                $('.'+pageName+'').addClass('selected');
            });
	function chkInputAdmn() {
	var keyword, location_name; 
	if($('.job_search_input').val() == 'interns') 
	{ 
		window.location = '/search/admin/intern-intern-results/?keyword='; 
