<script type="text/javascript">
	function hasValue() { 
		if($(".banner_input").val() == 'type a skill, industry, school or location')
			window.location = "/interns/?keyword=";
		else
			window.location = "/interns/?keyword="+ $('.banner_input').val();
	}
	
var rotatingTextArr = new Array();
$('#rotatingText li').each(function(i){
	rotatingTextArr [i] = $(this).text();
});
function rotateText()
{
    var $sentence = $('#bannertext .highlight');
    var index = $sentence.attr('rel');
            
    if ( undefined === index )
    {
        index = rotatingTextArr.length;
    };
    index = parseInt(index) + 1;
    if ( index >= rotatingTextArr.length )
    {
        index = 0;
    };
	$sentence
	.fadeOut(500, function(){
		$sentence
		.attr('rel', index)
		.html('&#9733; ' + rotatingTextArr[index] )
		.fadeIn(500);
	});            
};
            
setInterval ( rotateText, 4000 );
</script>
